contracts/CourseFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./CourseToken.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title CourseFactory
 * @dev 负责创建课程NFT和对应的课程代币
 */
contract CourseFactory is ERC721, Ownable {
    // 课程ID计数器
    uint256 private _courseIds;

    // 课程结构
    struct Course {
        uint256 id;
        string title;
        string description;
        uint256 duration; // 课程时长（小时）
        uint256 price; // 以wei为单位
        address creator;
        address courseTokenAddress;
        uint256 creationTime;
        uint256 learnersCount;
        bool isActive;
    }

    // 课程存储：课程ID => 课程结构
    mapping(uint256 => Course) public courses;
    
    // 创建者的课程列表：创作者地址 => 课程ID数组
    mapping(address => uint256[]) public creatorCourses;
    
    // NFT URI基础路径
    string private _baseTokenURI;
    
    // 平台费率（10 = 1%）
    uint256 public platformFeeRate = 100; // 10%
    
    // 平台费用接收地址
    address public feeCollector;
    
    // 行业系数 (100 = 1.00)
    mapping(string => uint256) public industryCoefficientMap;
    
    // 创作者声誉值 (100 = 1.00)
    mapping(address => uint256) public creatorReputationMap;

    // 事件
    event CourseCreated(uint256 indexed courseId, address creator, address courseTokenAddress);
    event CourseUpdated(uint256 indexed courseId);
    event CourseActivationChanged(uint256 indexed courseId, bool isActive);

    /**
     * @dev 检查课程（NFT）是否存在
     */
    function _exists(uint256 courseId) internal view returns (bool) {
        return courseId > 0 && courseId <= _courseIds && courses[courseId].creator != address(0);
    }

    constructor(address _feeCollector) ERC721("EDU Course Certificate", "EDUCERT") Ownable(msg.sender) {
        require(_feeCollector != address(0), "Invalid fee collector address");
        feeCollector = _feeCollector;
        
        // 设置一些默认行业系数
        industryCoefficientMap["technology"] = 120; // 1.20x
        industryCoefficientMap["business"] = 110; // 1.10x
        industryCoefficientMap["arts"] = 90; // 0.90x
        industryCoefficientMap["science"] = 115; // 1.15x
        
        // 所有创作者初始声誉为1.00
        creatorReputationMap[msg.sender] = 100;
    }

    /**
     * @dev 创建新课程及其对应的代币
     */
    function createCourse(
        string memory title,
        string memory description,
        uint256 duration,
        string memory industry,
        uint256 basePrice
    ) external returns (uint256) {
        require(bytes(title).length > 0, "Title cannot be empty");
        require(duration > 0, "Duration must be greater than 0");
        require(basePrice > 0, "Base price must be greater than 0");
        
        // 计算课程价格 = 基础价格 * 课程时长 * 行业系数 * 创作者声誉
        uint256 industryCoef = industryCoefficientMap[industry];
        if (industryCoef == 0) industryCoef = 100; // 默认系数为1
        
        uint256 creatorReputation = creatorReputationMap[msg.sender];
        if (creatorReputation == 0) creatorReputation = 100; // 默认声誉为1
        
        uint256 calculatedPrice = basePrice * duration * industryCoef * creatorReputation / 1000000; // 除以10^6因为系数乘以100
        
        // 创建课程代币
        uint256 tokenSupply = 1000000 * duration; // 基础供应量 = 100万 * 课程时长
        CourseToken courseToken = new CourseToken(
            string(abi.encodePacked(title, " Token")),
            string(abi.encodePacked("CT", _courseIds)),
            tokenSupply,
            msg.sender
        );
        
        // 创建课程记录
        _courseIds += 1;
        uint256 newCourseId = _courseIds;
        
        courses[newCourseId] = Course({
            id: newCourseId,
            title: title,
            description: description,
            duration: duration,
            price: calculatedPrice,
            creator: msg.sender,
            courseTokenAddress: address(courseToken),
            creationTime: block.timestamp,
            learnersCount: 0,
            isActive: true
        });
        
        // 添加到创作者的课程列表
        creatorCourses[msg.sender].push(newCourseId);
        
        // 铸造NFT给创作者（作为课程所有权证明）
        _mint(msg.sender, newCourseId);
        
        emit CourseCreated(newCourseId, msg.sender, address(courseToken));
        
        return newCourseId;
    }
    
    /**
     * @dev 更新课程信息
     */
    function updateCourse(
        uint256 courseId,
        string memory title,
        string memory description
    ) external {
        require(_exists(courseId), "Course does not exist");
        require(courses[courseId].creator == msg.sender, "Not the course creator");
        
        Course storage course = courses[courseId];
        course.title = title;
        course.description = description;
        
        emit CourseUpdated(courseId);
    }
    
    /**
     * @dev 更改课程激活状态
     */
    function toggleCourseActivation(uint256 courseId) external {
        require(_exists(courseId), "Course does not exist");
        require(courses[courseId].creator == msg.sender, "Not the course creator");
        
        courses[courseId].isActive = !courses[courseId].isActive;
        
        emit CourseActivationChanged(courseId, courses[courseId].isActive);
    }
    
    /**
     * @dev 获取创作者的所有课程ID
     */
    function getCreatorCourses(address creator) external view returns (uint256[] memory) {
        return creatorCourses[creator];
    }
    
    /**
     * @dev 获取课程详情
     */
    function getCourse(uint256 courseId) external view returns (Course memory) {
        require(_exists(courseId), "Course does not exist");
        return courses[courseId];
    }
    
    /**
     * @dev 设置平台费率
     */
    function setPlatformFeeRate(uint256 newRate) external onlyOwner {
        require(newRate <= 300, "Fee too high"); // 最大30%
        platformFeeRate = newRate;
    }
    
    /**
     * @dev 设置行业系数
     */
    function setIndustryCoefficient(string memory industry, uint256 coefficient) external onlyOwner {
        require(coefficient > 0 && coefficient <= 300, "Invalid coefficient"); // 0-3.00x
        industryCoefficientMap[industry] = coefficient;
    }
    
    /**
     * @dev 设置创作者声誉值
     */
    function setCreatorReputation(address creator, uint256 reputation) external onlyOwner {
        require(reputation > 0 && reputation <= 300, "Invalid reputation"); // 0-3.00x
        creatorReputationMap[creator] = reputation;
    }
    
    /**
     * @dev 设置基础令牌URI
     */
    function setBaseURI(string memory baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }
    
    /**
     * @dev 返回令牌URI
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }
}

contracts/CourseToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title CourseToken
 * @dev 课程代币实现，每个课程都有自己的代币
 */
contract CourseToken is ERC20, Ownable {
    // 代币分配
    uint256 private constant LIQUIDITY_POOL = 60; // 60%流动性池
    uint256 private constant CREATOR_ALLOCATION = 20; // 20%创作者保留
    uint256 private constant COMMUNITY_INCENTIVES = 20; // 20%社区激励池

    // 代币地址
    address public liquidityPoolAddress;
    address public communityIncentivesAddress;

    // 交易税率（以千分之一为单位）
    uint256 public tradeTaxRate = 20; // 2%
    
    // 交易税分配比例（以百分比为单位）
    uint256 public burnTaxPercentage = 50; // 50%销毁
    uint256 public scholarshipTaxPercentage = 50; // 50%奖学金池
    
    // 奖学金池地址
    address public scholarshipPoolAddress;
    
    // 波段交易优惠
    mapping(address => uint256) public holdingSince;
    
    // 事件
    event TaxCollected(address indexed from, address indexed to, uint256 taxAmount);
    event TaxDistributed(uint256 burnAmount, uint256 scholarshipAmount);

    constructor(
        string memory name,
        string memory symbol,
        uint256 totalSupply,
        address creator
    ) ERC20(name, symbol) Ownable(creator) {
        // 临时设置为创建者地址，在实际部署时应该设置为真实地址
        liquidityPoolAddress = creator;
        communityIncentivesAddress = creator;
        scholarshipPoolAddress = creator;
        
        // 分配代币
        _mint(liquidityPoolAddress, (totalSupply * LIQUIDITY_POOL) / 100);
        _mint(creator, (totalSupply * CREATOR_ALLOCATION) / 100);
        _mint(communityIncentivesAddress, (totalSupply * COMMUNITY_INCENTIVES) / 100);
        
        // 设置创建者的持有时间为创建时间
        holdingSince[creator] = block.timestamp;
    }

    /**
     * @dev 覆盖transfer方法以应用交易税
     */
    function transfer(address to, uint256 amount) public override returns (bool) {
        return _transferWithTax(msg.sender, to, amount);
    }

    /**
     * @dev 覆盖transferFrom方法以应用交易税
     */
    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        address spender = msg.sender;
        _spendAllowance(from, spender, amount);
        return _transferWithTax(from, to, amount);
    }

    /**
     * @dev 内部带税转账方法
     */
    function _transferWithTax(address from, address to, uint256 amount) internal returns (bool) {
        require(from != address(0), "Transfer from zero address");
        require(to != address(0), "Transfer to zero address");
        
        // 检查是否需要征税（创建者和系统地址免税）
        bool isTaxExempt = from == owner() || 
                           to == owner() || 
                           from == liquidityPoolAddress || 
                           to == liquidityPoolAddress || 
                           from == communityIncentivesAddress || 
                           to == communityIncentivesAddress || 
                           from == scholarshipPoolAddress;
        
        // 应用波段交易折扣
        uint256 effectiveTaxRate = tradeTaxRate;
        if (!isTaxExempt && holdingSince[from] > 0) {
            uint256 holdingDuration = block.timestamp - holdingSince[from];
            
            // 持有超过7天享受折扣
            if (holdingDuration > 7 days) {
                effectiveTaxRate = effectiveTaxRate * 80 / 100; // 20%折扣
            }
            
            // 持有超过30天享受更多折扣
            if (holdingDuration > 30 days) {
                effectiveTaxRate = effectiveTaxRate * 60 / 100; // 40%折扣
            }
        }
        
        // 计算税额
        uint256 taxAmount = 0;
        if (!isTaxExempt) {
            taxAmount = (amount * effectiveTaxRate) / 1000;
        }
        
        // 执行转账
        if (taxAmount > 0) {
            // 计算销毁金额和奖学金池金额
            uint256 burnAmount = (taxAmount * burnTaxPercentage) / 100;
            uint256 scholarshipAmount = taxAmount - burnAmount;
            
            // 执行实际转账
            super._transfer(from, address(this), taxAmount); // 先收取税
            super._transfer(from, to, amount - taxAmount); // 然后转账扣除税后的金额
            
            // 分配税收
            if (burnAmount > 0) {
                _burn(address(this), burnAmount);
            }
            
            if (scholarshipAmount > 0) {
                super._transfer(address(this), scholarshipPoolAddress, scholarshipAmount);
            }
            
            emit TaxCollected(from, to, taxAmount);
            emit TaxDistributed(burnAmount, scholarshipAmount);
        } else {
            super._transfer(from, to, amount);
        }
        
        // 更新接收者的持有时间
        holdingSince[to] = block.timestamp;
        
        return true;
    }
    
    /**
     * @dev 设置交易税率
     */
    function setTradeTaxRate(uint256 newRate) external onlyOwner {
        require(newRate <= 50, "Tax rate too high"); // 最高5%
        tradeTaxRate = newRate;
    }
    
    /**
     * @dev 设置税收分配比例
     */
    function setTaxDistribution(uint256 burnPercentage, uint256 scholarshipPercentage) external onlyOwner {
        require(burnPercentage + scholarshipPercentage == 100, "Percentages must sum to 100");
        burnTaxPercentage = burnPercentage;
        scholarshipTaxPercentage = scholarshipPercentage;
    }
    
    /**
     * @dev 设置地址
     */
    function setAddresses(
        address _liquidityPoolAddress,
        address _communityIncentivesAddress,
        address _scholarshipPoolAddress
    ) external onlyOwner {
        require(_liquidityPoolAddress != address(0), "Invalid liquidity pool address");
        require(_communityIncentivesAddress != address(0), "Invalid community incentives address");
        require(_scholarshipPoolAddress != address(0), "Invalid scholarship pool address");
        
        liquidityPoolAddress = _liquidityPoolAddress;
        communityIncentivesAddress = _communityIncentivesAddress;
        scholarshipPoolAddress = _scholarshipPoolAddress;
    }
    
    /**
     * @dev 获取持有者的持有时长（秒）
     */
    function getHoldingDuration(address holder) external view returns (uint256) {
        if (holdingSince[holder] == 0) {
            return 0;
        }
        return block.timestamp - holdingSince[holder];
    }
}

contracts/EDUToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title EDUToken
 * @dev 平台代币，用于治理和激励
 */
contract EDUToken is ERC20, ERC20Burnable, Ownable {
    // 空投锁定
    mapping(address => uint256) public airdropLock;
    
    // 每次销毁的百分比分配给奖学金池（5%）
    uint256 public constant SCHOLARSHIP_PERCENT = 5;
    
    // 奖学金池地址
    address public scholarshipPool;
    
    // 事件
    event ScholarshipFunded(uint256 amount);
    event AirdropLocked(address indexed recipient, uint256 amount, uint256 unlockTime);
    event AirdropClaimed(address indexed recipient, uint256 amount);
    
    constructor(string memory name, string memory symbol, uint256 initialSupply) ERC20(name, symbol) Ownable(msg.sender) {
        // 初始代币分配
        _mint(msg.sender, initialSupply);
        
        // 设置奖学金池（此处简化为合约自身地址，实际应为多签钱包）
        scholarshipPool = address(this);
    }
    
    /**
     * @dev 设置奖学金池地址
     */
    function setScholarshipPool(address _scholarshipPool) external onlyOwner {
        require(_scholarshipPool != address(0), "Invalid scholarship pool address");
        scholarshipPool = _scholarshipPool;
    }
    
    /**
     * @dev 铸造新代币（仅限所有者）
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }
    
    /**
     * @dev 销毁代币并将一部分转入奖学金池
     */
    function burnWithScholarship(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        
        // 计算奖学金池分配
        uint256 scholarshipAmount = (amount * SCHOLARSHIP_PERCENT) / 100;
        uint256 burnAmount = amount - scholarshipAmount;
        
        // 转账到奖学金池
        _transfer(msg.sender, scholarshipPool, scholarshipAmount);
        
        // 销毁剩余部分
        _burn(msg.sender, burnAmount);
        
        emit ScholarshipFunded(scholarshipAmount);
    }
    
    /**
     * @dev 锁定空投代币
     */
    function lockAirdrop(address recipient, uint256 amount, uint256 lockDuration) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        require(lockDuration > 0, "Lock duration must be greater than 0");
        
        uint256 unlockTime = block.timestamp + lockDuration;
        airdropLock[recipient] = unlockTime;
        
        // 转账代币到接收者地址
        _transfer(msg.sender, recipient, amount);
        
        emit AirdropLocked(recipient, amount, unlockTime);
    }
    
    /**
     * @dev 重写transfer函数，添加锁定检查
     */
    function transfer(address to, uint256 amount) public override returns (bool) {
        // 检查发送者的锁定状态
        if (airdropLock[msg.sender] > 0) {
            require(block.timestamp >= airdropLock[msg.sender], "Tokens are locked");
            // 解锁完成后清除锁定记录
            if (block.timestamp >= airdropLock[msg.sender]) {
                airdropLock[msg.sender] = 0;
            }
        }
        
        return super.transfer(to, amount);
    }
    
    /**
     * @dev 重写transferFrom函数，添加锁定检查
     */
    function transferFrom(address from, address to, uint256 amount) public override returns (bool) {
        // 检查发送者的锁定状态
        if (airdropLock[from] > 0) {
            require(block.timestamp >= airdropLock[from], "Tokens are locked");
            // 解锁完成后清除锁定记录
            if (block.timestamp >= airdropLock[from]) {
                airdropLock[from] = 0;
            }
        }
        
        return super.transferFrom(from, to, amount);
    }
}

contracts/LearningManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./CourseFactory.sol";

/**
 * @title LearningManager
 * @dev 负责管理学习者的学习过程、质押和奖励
 */
contract LearningManager is Ownable, ReentrancyGuard {
    // 课程工厂合约
    CourseFactory public courseFactory;
    
    // 学习记录
    struct LearningRecord {
        uint256 courseId;
        address learner;
        uint256 stakedAmount;
        uint256 startTime;
        uint256 lastCheckpointTime;
        uint256 progressPercent; // 0-100
        bool completed;
        bool isActive;
    }
    
    // 学习记录ID计数器 - 使用简单的uint256替代Counters
    uint256 private _recordIds;
    
    // 学习记录映射：记录ID => 学习记录
    mapping(uint256 => LearningRecord) public learningRecords;
    
    // 学习者的学习记录：学习者地址 => 课程ID => 记录ID
    mapping(address => mapping(uint256 => uint256)) public learnerCourseRecords;
    
    // 课程的学习者列表：课程ID => 学习者地址数组
    mapping(uint256 => address[]) public courseLearners;
    
    // NFT证书映射：课程ID => 学习者地址 => 是否已颁发
    mapping(uint256 => mapping(address => bool)) public certificatesIssued;
    
    // 测验成绩：记录ID => 成绩（0-100）
    mapping(uint256 => uint256) public quizScores;
    
    // 学习奖励比例（最高70%返还）
    uint256 public constant MAX_REWARD_PERCENTAGE = 70;
    
    // 平台代币地址
    address public platformTokenAddress;
    
    // 证书NFT合约地址
    address public certificateNFTAddress;
    
    // 事件
    event CourseEnrolled(uint256 indexed recordId, uint256 indexed courseId, address indexed learner, uint256 stakedAmount);
    event ProgressUpdated(uint256 indexed recordId, uint256 newProgress);
    event CourseCompleted(uint256 indexed recordId, uint256 indexed courseId, address indexed learner, uint256 rewardAmount);
    event CertificateIssued(uint256 indexed courseId, address indexed learner);
    event QuizSubmitted(uint256 indexed recordId, uint256 score);
    
    constructor(address _courseFactoryAddress, address _platformTokenAddress) Ownable(msg.sender) {
        require(_courseFactoryAddress != address(0), "Invalid course factory address");
        require(_platformTokenAddress != address(0), "Invalid platform token address");
        
        courseFactory = CourseFactory(_courseFactoryAddress);
        platformTokenAddress = _platformTokenAddress;
    }
    
    /**
     * @dev 设置证书NFT合约地址
     */
    function setCertificateNFTAddress(address _certificateNFTAddress) external onlyOwner {
        require(_certificateNFTAddress != address(0), "Invalid certificate NFT address");
        certificateNFTAddress = _certificateNFTAddress;
    }
    
    /**
     * @dev 学习者报名课程
     */
    function enrollCourse(uint256 courseId) external payable nonReentrant returns (uint256) {
        // 获取课程信息
        CourseFactory.Course memory course = courseFactory.getCourse(courseId);
        require(course.isActive, "Course is not active");
        
        // 检查是否已报名
        require(learnerCourseRecords[msg.sender][courseId] == 0, "Already enrolled");
        
        // 检查支付金额
        require(msg.value >= course.price, "Insufficient payment");
        
        // 创建学习记录 - 修改计数器递增逻辑
        _recordIds += 1;
        uint256 newRecordId = _recordIds;
        
        learningRecords[newRecordId] = LearningRecord({
            courseId: courseId,
            learner: msg.sender,
            stakedAmount: msg.value,
            startTime: block.timestamp,
            lastCheckpointTime: block.timestamp,
            progressPercent: 0,
            completed: false,
            isActive: true
        });
        
        // 更新映射
        learnerCourseRecords[msg.sender][courseId] = newRecordId;
        courseLearners[courseId].push(msg.sender);
        
        // 转发部分资金到课程创建者
        uint256 platformFee = (msg.value * courseFactory.platformFeeRate()) / 1000;
        uint256 creatorPayout = msg.value - platformFee;
        
        // 发送给创建者
        payable(course.creator).transfer(creatorPayout);
        
        emit CourseEnrolled(newRecordId, courseId, msg.sender, msg.value);
        
        return newRecordId;
    }
    
    /**
     * @dev 更新学习进度
     * @notice 仅由预言机或管理员调用
     */
    function updateProgress(uint256 recordId, uint256 newProgress) external onlyOwner {
        require(recordId <= _recordIds, "Record does not exist");
        require(newProgress <= 100, "Progress cannot exceed 100%");
        
        LearningRecord storage record = learningRecords[recordId];
        require(record.isActive, "Record is not active");
        require(!record.completed, "Course already completed");
        
        // 只能增加进度
        require(newProgress > record.progressPercent, "Cannot decrease progress");
        
        record.progressPercent = newProgress;
        record.lastCheckpointTime = block.timestamp;
        
        emit ProgressUpdated(recordId, newProgress);
        
        // 如果进度达到100%，标记为完成
        if (newProgress == 100) {
            completeCourse(recordId);
        }
    }
    
    /**
     * @dev 提交测验成绩
     * @notice 仅由预言机或管理员调用
     */
    function submitQuizScore(uint256 recordId, uint256 score) external onlyOwner {
        require(recordId <= _recordIds, "Record does not exist");
        require(score <= 100, "Score cannot exceed 100");
        
        LearningRecord storage record = learningRecords[recordId];
        require(record.isActive, "Record is not active");
        
        quizScores[recordId] = score;
        
        emit QuizSubmitted(recordId, score);
    }
    
    /**
     * @dev 完成课程
     */
    function completeCourse(uint256 recordId) internal {
        LearningRecord storage record = learningRecords[recordId];
        record.completed = true;
        record.progressPercent = 100;
        
        // 计算奖励
        uint256 reward = calculateReward(recordId);
        
        // 发放奖励
        if (reward > 0) {
            payable(record.learner).transfer(reward);
        }
        
        // 颁发证书
        issueCertificate(record.courseId, record.learner);
        
        emit CourseCompleted(recordId, record.courseId, record.learner, reward);
    }
    
    /**
     * @dev 计算奖励金额
     */
    function calculateReward(uint256 recordId) public view returns (uint256) {
        LearningRecord memory record = learningRecords[recordId];
        
        // 基础返还比例 - 完成度 * 最高返还比例
        uint256 baseRewardPercentage = (record.progressPercent * MAX_REWARD_PERCENTAGE) / 100;
        
        // 考虑测验成绩 - 测验分数影响10%的返还比例
        uint256 scoreBonus = 0;
        uint256 score = quizScores[recordId];
        if (score > 0) {
            scoreBonus = (score * 10) / 100; // 满分可额外获得10%返还
        }
        
        // 总返还比例
        uint256 totalRewardPercentage = baseRewardPercentage + scoreBonus;
        if (totalRewardPercentage > MAX_REWARD_PERCENTAGE) {
            totalRewardPercentage = MAX_REWARD_PERCENTAGE;
        }
        
        // 计算返还金额
        return (record.stakedAmount * totalRewardPercentage) / 100;
    }
    
    /**
     * @dev 颁发证书
     */
    function issueCertificate(uint256 courseId, address learner) internal {
        // 检查是否已颁发
        if (certificatesIssued[courseId][learner]) {
            return;
        }
        
        certificatesIssued[courseId][learner] = true;
        
        // 如果设置了证书NFT合约，铸造NFT
        if (certificateNFTAddress != address(0)) {
            // 这里应该调用NFT合约的铸造方法
            // 简化起见，仅触发事件
            emit CertificateIssued(courseId, learner);
        }
    }
    
    /**
     * @dev 获取学习记录
     */
    function getLearningRecord(uint256 recordId) external view returns (LearningRecord memory) {
        require(recordId <= _recordIds, "Record does not exist");
        return learningRecords[recordId];
    }
    
    /**
     * @dev 获取学习者的课程记录ID
     */
    function getLearnerCourseRecord(address learner, uint256 courseId) external view returns (uint256) {
        return learnerCourseRecords[learner][courseId];
    }
    
    /**
     * @dev 获取课程的所有学习者
     */
    function getCourseLearners(uint256 courseId) external view returns (address[] memory) {
        return courseLearners[courseId];
    }
    
    /**
     * @dev 检查学习者是否有特定课程的证书
     */
    function hasCertificate(uint256 courseId, address learner) external view returns (bool) {
        return certificatesIssued[courseId][learner];
    }
    
    /**
     * @dev Learn-to-Earn奖励公式
     */
    function calculateLTE(uint256 recordId) external view returns (uint256) {
        LearningRecord memory record = learningRecords[recordId];
        
        // 获取课程信息
        CourseFactory.Course memory course = courseFactory.getCourse(record.courseId);
        
        // 计算学习时长（小时）
        uint256 learningDuration = (block.timestamp - record.startTime) / 3600;
        
        // 获取测验成绩
        uint256 score = quizScores[recordId];
        
        // 课程难度系数（简化为课程时长，更复杂的逻辑可以扩展）
        uint256 difficultyCoefficent = course.duration;
        
        // 计算Learn-to-Earn奖励
        // 奖励 = (学习时长 * 测验成绩%) / 课程难度系数
        if (score == 0 || difficultyCoefficent == 0) {
            return 0;
        }
        
        return (learningDuration * score * 10**18) / (difficultyCoefficent * 100);
    }
    
    /**
     * @dev 接收ETH
     */
    receive() external payable {}
}

contracts/LiquidityPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title LiquidityPool
 * @dev 课程代币与ETH的流动性池
 */
contract LiquidityPool is Ownable, ReentrancyGuard {
    using Math for uint256;

    // 常量
    uint256 private constant MINIMUM_LIQUIDITY = 10**3;
    uint256 private constant FEE_DENOMINATOR = 1000;
    
    // 交易手续费 (3 = 0.3%)
    uint256 public tradingFee = 3;
    
    // 特殊池激励 (300 = 3倍收益)
    uint256 public specialPoolMultiplier = 300;
    
    // 平台代币地址
    address public platformTokenAddress;
    
    // 代币储备
    mapping(address => uint256) public tokenReserves;
    mapping(address => uint256) public ethReserves;
    
    // 流动性提供者代币余额
    mapping(address => mapping(address => uint256)) public lpBalances;
    
    // 总流动性代币供应量
    mapping(address => uint256) public totalLpTokens;
    
    // 冷门池标记（课程代币地址=>是否冷门）
    mapping(address => bool) public isSpecialPool;
    
    // 流动性锁定时间（课程代币地址=>解锁时间戳）
    mapping(address => uint256) public liquidityUnlockTime;
    
    // 事件
    event LiquidityAdded(address indexed provider, address indexed tokenAddress, uint256 tokenAmount, uint256 ethAmount, uint256 lpTokens);
    event LiquidityRemoved(address indexed provider, address indexed tokenAddress, uint256 tokenAmount, uint256 ethAmount, uint256 lpTokens);
    event Swap(address indexed user, address indexed tokenAddress, uint256 tokenIn, uint256 ethIn, uint256 tokenOut, uint256 ethOut);
    event PoolCreated(address indexed tokenAddress, uint256 initialTokenAmount, uint256 initialEthAmount);
    
    constructor(address _platformTokenAddress) Ownable(msg.sender) {
        require(_platformTokenAddress != address(0), "Invalid platform token address");
        platformTokenAddress = _platformTokenAddress;
    }
    
    /**
     * @dev 创建新的流动性池
     */
    function createPool(address tokenAddress, uint256 tokenAmount, bool isSpecial) external payable nonReentrant returns (bool) {
        require(tokenAddress != address(0), "Invalid token address");
        require(totalLpTokens[tokenAddress] == 0, "Pool already exists");
        require(tokenAmount > 0 && msg.value > 0, "Insufficient liquidity");
        
        // 标记特殊池
        if (isSpecial) {
            isSpecialPool[tokenAddress] = true;
        }
        
        // 初始流动性锁定48小时
        liquidityUnlockTime[tokenAddress] = block.timestamp + 48 hours;
        
        // 转移代币到合约
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), tokenAmount);
        
        // 计算初始代币，减去MINIMUM_LIQUIDITY防止首次流动性过低
        uint256 initialLpTokens = Math.sqrt(tokenAmount * msg.value) - MINIMUM_LIQUIDITY;
        
        // 更新状态
        tokenReserves[tokenAddress] = tokenAmount;
        ethReserves[tokenAddress] = msg.value;
        lpBalances[tokenAddress][msg.sender] = initialLpTokens;
        totalLpTokens[tokenAddress] = initialLpTokens + MINIMUM_LIQUIDITY;
        
        emit PoolCreated(tokenAddress, tokenAmount, msg.value);
        emit LiquidityAdded(msg.sender, tokenAddress, tokenAmount, msg.value, initialLpTokens);
        
        return true;
    }
    
    /**
     * @dev 添加流动性
     */
    function addLiquidity(address tokenAddress, uint256 tokenAmount) external payable nonReentrant returns (uint256) {
        require(tokenAddress != address(0), "Invalid token address");
        require(totalLpTokens[tokenAddress] > 0, "Pool does not exist");
        require(tokenAmount > 0 && msg.value > 0, "Insufficient liquidity");
        
        uint256 _tokenReserve = tokenReserves[tokenAddress];
        uint256 _ethReserve = ethReserves[tokenAddress];
        
        // 计算应提供的代币数量与ETH按比例
        uint256 ethToToken = (msg.value * _tokenReserve) / _ethReserve;
        uint256 tokenToEth = (tokenAmount * _ethReserve) / _tokenReserve;
        
        // 确定最终添加的数量
        uint256 finalTokenAmount;
        uint256 finalEthAmount;
        
        if (tokenToEth <= msg.value) {
            // 代币是限制因素
            finalTokenAmount = tokenAmount;
            finalEthAmount = tokenToEth;
        } else {
            // ETH是限制因素
            finalTokenAmount = ethToToken;
            finalEthAmount = msg.value;
        }
        
        // 退还多余的ETH
        if (finalEthAmount < msg.value) {
            payable(msg.sender).transfer(msg.value - finalEthAmount);
        }
        
        // 转移代币到合约
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), finalTokenAmount);
        
        // 计算LP代币数量
        uint256 lpTokens = (finalTokenAmount * totalLpTokens[tokenAddress]) / _tokenReserve;
        
        // 更新状态
        tokenReserves[tokenAddress] += finalTokenAmount;
        ethReserves[tokenAddress] += finalEthAmount;
        lpBalances[tokenAddress][msg.sender] += lpTokens;
        totalLpTokens[tokenAddress] += lpTokens;
        
        emit LiquidityAdded(msg.sender, tokenAddress, finalTokenAmount, finalEthAmount, lpTokens);
        
        return lpTokens;
    }
    
    /**
     * @dev 移除流动性
     */
    function removeLiquidity(address tokenAddress, uint256 lpTokenAmount) external nonReentrant returns (uint256, uint256) {
        require(tokenAddress != address(0), "Invalid token address");
        require(lpTokenAmount > 0, "Invalid LP token amount");
        require(lpBalances[tokenAddress][msg.sender] >= lpTokenAmount, "Insufficient LP tokens");
        
        // 检查锁定期是否已过
        require(block.timestamp >= liquidityUnlockTime[tokenAddress], "Liquidity still locked");
        
        uint256 tokenAmount = (lpTokenAmount * tokenReserves[tokenAddress]) / totalLpTokens[tokenAddress];
        uint256 ethAmount = (lpTokenAmount * ethReserves[tokenAddress]) / totalLpTokens[tokenAddress];
        
        // 更新状态
        lpBalances[tokenAddress][msg.sender] -= lpTokenAmount;
        totalLpTokens[tokenAddress] -= lpTokenAmount;
        tokenReserves[tokenAddress] -= tokenAmount;
        ethReserves[tokenAddress] -= ethAmount;
        
        // 转移资产
        IERC20(tokenAddress).transfer(msg.sender, tokenAmount);
        payable(msg.sender).transfer(ethAmount);
        
        emit LiquidityRemoved(msg.sender, tokenAddress, tokenAmount, ethAmount, lpTokenAmount);
        
        return (tokenAmount, ethAmount);
    }
    
    /**
     * @dev 计算交易输出
     */
    function getAmountOut(uint256 amountIn, uint256 reserveIn, uint256 reserveOut) public view returns (uint256) {
        require(amountIn > 0, "Insufficient input amount");
        require(reserveIn > 0 && reserveOut > 0, "Insufficient liquidity");
        
        uint256 amountInWithFee = amountIn * (1000 - tradingFee);
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = (reserveIn * 1000) + amountInWithFee;
        
        return numerator / denominator;
    }
    
    /**
     * @dev ETH换代币
     */
    function swapETHForToken(address tokenAddress, uint256 minTokenOut, address recipient) external payable nonReentrant returns (uint256) {
        require(tokenAddress != address(0), "Invalid token address");
        require(totalLpTokens[tokenAddress] > 0, "Pool does not exist");
        require(msg.value > 0, "Must send ETH");
        
        uint256 tokenReserve = tokenReserves[tokenAddress];
        uint256 ethReserve = ethReserves[tokenAddress];
        
        uint256 tokenOut = getAmountOut(msg.value, ethReserve, tokenReserve);
        
        require(tokenOut >= minTokenOut, "Insufficient output amount");
        
        // 更新储备
        tokenReserves[tokenAddress] = tokenReserve - tokenOut;
        ethReserves[tokenAddress] = ethReserve + msg.value;
        
        // 转移代币
        IERC20(tokenAddress).transfer(recipient, tokenOut);
        
        emit Swap(msg.sender, tokenAddress, 0, msg.value, tokenOut, 0);
        
        return tokenOut;
    }
    
    /**
     * @dev 代币换ETH
     */
    function swapTokenForETH(address tokenAddress, uint256 tokenIn, uint256 minEthOut) external nonReentrant returns (uint256) {
        require(tokenAddress != address(0), "Invalid token address");
        require(totalLpTokens[tokenAddress] > 0, "Pool does not exist");
        require(tokenIn > 0, "Must send tokens");
        
        uint256 tokenReserve = tokenReserves[tokenAddress];
        uint256 ethReserve = ethReserves[tokenAddress];
        
        uint256 ethOut = getAmountOut(tokenIn, tokenReserve, ethReserve);
        
        require(ethOut >= minEthOut, "Insufficient output amount");
        
        // 更新储备
        tokenReserves[tokenAddress] = tokenReserve + tokenIn;
        ethReserves[tokenAddress] = ethReserve - ethOut;
        
        // 转移资产
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), tokenIn);
        payable(msg.sender).transfer(ethOut);
        
        emit Swap(msg.sender, tokenAddress, tokenIn, 0, 0, ethOut);
        
        return ethOut;
    }
    
    /**
     * @dev 获取池子信息
     */
    function getPoolInfo(address tokenAddress) external view returns (
        uint256 tokenReserve,
        uint256 ethReserve,
        uint256 totalLp,
        bool isSpecial,
        uint256 unlockTime
    ) {
        return (
            tokenReserves[tokenAddress],
            ethReserves[tokenAddress],
            totalLpTokens[tokenAddress],
            isSpecialPool[tokenAddress],
            liquidityUnlockTime[tokenAddress]
        );
    }
    
    /**
     * @dev 获取流动性提供者信息
     */
    function getLpInfo(address tokenAddress, address provider) external view returns (
        uint256 lpBalance,
        uint256 tokenShare,
        uint256 ethShare
    ) {
        uint256 lpBalance_ = lpBalances[tokenAddress][provider];
        
        return (
            lpBalance_,
            (lpBalance_ * tokenReserves[tokenAddress]) / totalLpTokens[tokenAddress],
            (lpBalance_ * ethReserves[tokenAddress]) / totalLpTokens[tokenAddress]
        );
    }
    
    /**
     * @dev 设置交易手续费
     */
    function setTradingFee(uint256 newFee) external onlyOwner {
        require(newFee <= 30, "Fee too high"); // 最高3%
        tradingFee = newFee;
    }
    
    /**
     * @dev 设置冷门池倍率
     */
    function setSpecialPoolMultiplier(uint256 newMultiplier) external onlyOwner {
        require(newMultiplier >= 100, "Multiplier too low"); // 至少1倍
        require(newMultiplier <= 500, "Multiplier too high"); // 最多5倍
        specialPoolMultiplier = newMultiplier;
    }
    
    /**
     * @dev 标记特殊池
     */
    function markSpecialPool(address tokenAddress, bool isSpecial) external onlyOwner {
        require(totalLpTokens[tokenAddress] > 0, "Pool does not exist");
        isSpecialPool[tokenAddress] = isSpecial;
    }
    
    /**
     * @dev 价格熔断机制 - 检查价格波动
     */
    function checkPriceVolatility(address tokenAddress, uint256 amountIn, bool isEthToToken) external view returns (bool isTooVolatile) {
        uint256 tokenReserve = tokenReserves[tokenAddress];
        uint256 ethReserve = ethReserves[tokenAddress];
        
        // 计算交易后的价格
        uint256 amountOut;
        uint256 newPrice;
        uint256 oldPrice = (ethReserve * 1e18) / tokenReserve; // 当前ETH/代币价格（放大1e18倍）
        
        if (isEthToToken) {
            amountOut = getAmountOut(amountIn, ethReserve, tokenReserve);
            newPrice = ((ethReserve + amountIn) * 1e18) / (tokenReserve - amountOut);
        } else {
            amountOut = getAmountOut(amountIn, tokenReserve, ethReserve);
            newPrice = ((ethReserve - amountOut) * 1e18) / (tokenReserve + amountIn);
        }
        
        // 计算价格变化百分比
        uint256 priceChange;
        if (newPrice > oldPrice) {
            priceChange = ((newPrice - oldPrice) * 100) / oldPrice;
        } else {
            priceChange = ((oldPrice - newPrice) * 100) / oldPrice;
        }
        
        // 波动超过20%则视为过于波动
        return priceChange > 20;
    }
    
    /**
     * @dev 接收ETH
     */
    receive() external payable {}
}

